function tabMake(){
    var menu = document.getElementById('tab_menu1');
    var content = document.getElementById('tab_content1');
    var menus = menu.getElementsByTagName('a');
    var current; // 現在の状態を保持する変数
    for (var i = 0, l = menus.length;i < l; i++){
        tab_init(menus[i], i);
    }
    function tab_init(link, index){
        var id = link.hash.slice(1);
        var page = document.getElementById(id);
        if (!current){ // 状態の初期化
            current = {page:page, menu:link};
            page.style.display = 'block';
            link.className = 'active';
        } else {
            page.style.display = 'none';
        }
        link.onclick = function(){
            current.page.style.display = 'none';
            current.menu.className = '';
            page.style.display = 'block';
            link.className = 'active';
            current.page = page;
            current.menu = link;
            return false;
        };
    }
    star();
}
window.onload = tabMake;
function star()
{
    var ooo =document.getElementsByName("base");
    var foo ="<center><hr><h3><font color=blue>日記帳</font></h3><h4>現在時刻 : ～～</h4><hr></center>";
    var bar ='<div class=Diary name="Diary"></div>';
    foo = foo.concat(bar);
    ooo[0].innerHTML = foo;

    var art = "";
    var art01 ='<form class =Article name="TodaysEvents">TodaysEvents</form>'
    var art02 ='<form class =Article name="TodaysNews">TodaysNews</form>'
    var art03 ='<div class =Article name="TodaysSchedule">TodaysSchedule</div>'
    var art04 ='<div class =Article name="ExperienceAndFeel">ExperienceAndFeel</div>'
    art = art.concat(art01);
    art = art.concat(art02);
    art = art.concat(art03);
    art = art.concat(art04);

    var KKK =document.getElementsByName("Diary");
    KKK[0].innerHTML = art;
}

